INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Polpettine di tonno e ricotta", "Antipasti", "Tonno sott'olio sgocciolato, Ricotta vaccina, Acciughe filetti sott'olio, Capperi sotto sale, Pangrattato, Prezzemolo, Parmigiano Reggiano DOP, Pepe nero", 10.50, "polpettine", "Le polpettine di tonno e ricotta sono una ricetta molto appetitosa, indicate da servire calde sia come antipasto che come secondo piatto.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Arancini di riso", "Antipasti", "Zafferano, burro, riso, maiale, pomodoro, pisellini, caciocavallo, vino rosso", 2.50, "arancini", "Gli arancini di riso sono dei piccoli timballi adatti ad essere consumati sia come spuntino che come antipasto.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Mozzarella in carrozza", "Antipasti", "Pane bianco, mozzarella di bufala, farina 00, uova", 3.50, "mozzarella", "Solo uno chef di origini partenopee poteva rivelarci tutti i segreti per realizzare la mozzarella in carrozza: una ricetta sfiziosa, molto semplice da preparare e che richiede piccoli accorgimenti per ottenere un risultato perfetto.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Muffin salati con piselli e prosciutto", "Antipasti", "Pisellini, prosciutto cotto, farina 00, latte, uova, prezzemolo, olio extravergine d'oliva", 12.50, "muffin", "Queste tortine soffici e saporite di origine anglosassone hanno conquistato anche noi, a volte anche solo come ispirazione per ricette particolari.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Flan di zucca", "Antipasti", "Zucca, parmigiano, pepe, latte intero, rosmarino, noce moscata, uova", 18.50, "flan", "I flan di zucca sono piccole delizie dal sapore dolce e delicato, ideali come aperitivo sfizioso. In contrasto con il gusto della polpa di zucca, abbiamo guarnito i flan con una crema al caprino un formaggio dal sapore deciso e piacevolmente acidulo.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Involtini di verza", "Antipasti", "Riso, verza, salsiccia, sedano, carote, cipolle, pepe", 8.50, "involtini", "Avete voglia di qualcosa di originale? Allora dovete provare questi involtini di verza! Il ricco e gustoso risotto alla salsiccia viene avvolto dalle foglie di questo ortaggio molto versatile: un’idea originale e molto sfiziosa.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Strudel salato", "Antipasti", "Pasta sfoglia, pancetta, finocchietto, pinoli, prugne secche, aglio", 22.50, "strudel", "Dentro un fragrante scrigno di sfoglia le succose prugne secche si abbinano perfettamente alla sapida rotondità di pancetta e salsiccia. Unisce tutti gli altri ingredienti in un mix francamente irresistibile.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Risotto con piselli e menta ", "Primi", "Pisellini freschi, riso Carnaroli, cipolla, menta, olio extravergine d'oliva, parmigiano", 32.50, "risotto_piselli", " Il risotto con piselli e menta ha un colore bellissimo, e ti assicuro che il connubio tra la dolcezza dei piselli e la freschezza della menta ti stupirà!");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Vellutata di carote e zenzero", "Primi", "Carote, patate, cipolle, zenzero, brodo vegetale, panna fresca, timo", 2.50, "vellutata", "Ottima durante la stagione invernale per una calda coccola ma anche durante l'estate per un tocco di energia.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Roselline di pasta fresca con salmone e pomodorini gialli", "Primi", "Lasagne di pasta fresca, salmone, pomodorini gialli, latte intero, farina 00, noce moscata", 25.50, "roselline", "Prova questa golosa teglia di roselline di pasta fresca con salmone e pomodorini gialli. Sfiziose e colorate, saranno perfette da servire anche nei giorni più festosi dell’anno, tra Natale e Capodanno.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Crema di castagne con finferli e speck", "Primi", "Castagne, finferli, speck, aglio, erba cipollina, patate", 28.50, "crema_castagne", "Questa crema, con il suo sapore avvolgente e goloso, saprà conquistare tutti al primo assaggio.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Tacchino ripieno", "Secondi", "Tacchino, rosmarino, alloro, carote, aglio, pepe. salvoa, uova, pane, mele", 48.50, "tacchino", "Sontuoso, incredibilmente ricco e così buono che – nonostante le dimensioni imponenti – sparirà in un attimo!");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Tonno in crosta di pistacchio", "Secondi", "Tonno, pistacchio, albumi, olio extravergine d'oliva", 28.50, "tonno", "Questa è una ricetta originale e sfiziosa creata per un progetto a cui tengo molto e che per me è sempre fonte di ispirazione.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Involtini di tacchino con riso e verdure", "Secondi", "Riso, melanzane, peperoni, zucchine, tacchino, erba cipollina", 37.50, "involtini", "Un secondo piatto delicato ma, allo stesso tempo, di gran gusto.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Spiedini di polpo e calamari", "Secondi", "Polpo, calamari, aglio, olio extravergine d'oliva, pepe bianco", 32.50, "spiedini_polpo", "Originali e gustosi con tutto il sapore del mare.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Kofta", "Secondi", "Carne di agnello, cipolle, aglio, pinoli, paprika, cannella, prezzemolo, noce moscata, tahina, limoni", 25.50, "kofta", "Chiudi gli occhi assaggiandoli e sarà come tuffarsi per un istante nel caos eccentrico delle bancarelle del mercato di Beirut!");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Hummus di zucca", "Contorni", "Ceci, zucca, thaina, paprika", 15.50, "hummus", "Accompagnato a verdure fresche è una perfetta alternativa al pinzimonio, spalmato su crostoni di pane tostato diventa irresistibile.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Composta di cachi", "Contorni", "Cachi, zucchero, limoni, pane", 12.50, "composta", "Un contorno tutto autunnale riempito fino all’orlo da un vivido colore arancione; il profumo che sprigiona è dolce, dall’aroma inconfondibile di cachi appena colti.");
INSERT INTO smartrestaurant.Piatto(nome, categoria, ingredienti, prezzo, foto, descrizione) VALUES ("Insalata di quinoa", "Contorni", "Quinoa, zucchine, erba cipollina, pinoli, timo, carote", 25.50, "quinoa", "Conosci la quinoa? Partiamo dal principio: questa pianta coltivata tra Bolivia e Perù ha una storia lunga più di 5 mila anni e, in tempi molto antichi, veniva venerata dagli Inca in quanto considerata la “madre di tutti i semi”.");
